"use strict";class HamburgerMenus{constructor(options){const hamburgerMenus=[],hamburgers=document.body.querySelectorAll(options.hamburger);hamburgers.forEach(function(hamburger,i){hamburgerMenus[i]=new HamburgerMenu(options,i)});return hamburgerMenus}}class HamburgerMenu{constructor(options,num=0){this.breakPoint=options.breakpoint||768;this.hamburger=document.body.querySelectorAll(options.hamburger)[num];this.menu=this.hamburger.parentElement.querySelector(options.menu);if(!this.menu){console.error('Не найден парный элемент "menu" для элемента "hamburger"')}this.hamburger.addEventListener('click',()=>this.toggle());window.addEventListener('click',e=>this.closeByOuterClick(e));window.addEventListener('load',()=>this.toggleByResolution());window.addEventListener('resize',()=>this.toggleByResolution());window.addEventListener('scroll',()=>this.toggleByResolution())}close(){this.hamburger.classList.remove('open');this.menu.classList.remove('show')}open(){this.hamburger.classList.add('open');this.menu.classList.add('show')}isOpen(){return this.menu.classList.contains('show')}closeByOuterClick(e){if(document.documentElement.clientWidth<this.breakPoint&&e.target!=this.hamburger&&e.target.parentElement!=this.hamburger&&e.target.parentElement.parentElement!=this.hamburger&&e.target!=this.menu&&e.target.parentElement!=this.menu&&e.target.parentElement.parentElement!=this.menu&&e.target.parentElement.parentElement.parentElement!=this.menu&&this.isOpen()){this.close()}}toggle(){this.isOpen()?this.close():this.open()}toggleByResolution(){if(document.documentElement.clientWidth<this.breakPoint){if(this.isOpen()){this.close()}if(!this.hamburger.classList.contains('show')){this.hamburger.classList.add('show')}}else{if(!this.isOpen()){this.open()}if(this.hamburger.classList.contains('show')){this.hamburger.classList.remove('show')}}}}